from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message

from stw_gen_sample3_api.types.core.can.channel_stat_type import ChannelStat


@dataclass
class ChannelStats(_Message):

    # Статистика по каналу Ctrl
    ctrl: ChannelStat
    # Статистика по каналу ReqRep (запросы)
    reqs: ChannelStat
    # Статистика по каналу ReqRep (ответы)
    reps: ChannelStat
    # Статистика по каналу Tm
    tm: ChannelStat
    # Статистика по каналу Stream
    stream: ChannelStat


class ChannelStatsDict(TypedDict):

    # Статистика по каналу Ctrl
    ctrl: Union[ChannelStat]

    # Статистика по каналу ReqRep (запросы)
    reqs: Union[ChannelStat]

    # Статистика по каналу ReqRep (ответы)
    reps: Union[ChannelStat]

    # Статистика по каналу Tm
    tm: Union[ChannelStat]

    # Статистика по каналу Stream
    stream: Union[ChannelStat]

