from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class ErrorMode(EnumType):
    """Режим поведения CAN-драйвера при возникновении ошибок"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # CAN в состоянии Error Active
    ActiveError = 0
    # CAN в состоянии Error Passive
    PassiveError = 1
    # CAN в состоянии Bus Off
    BusOff = 2
