from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class Mode(EnumType):
    """Режим работы CAN-драйвера"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Приём и передача CAN-сообщений включены
    Normal = 0
    # Передача CAN-сообщений отключена, сообщения принимаются
    Silent = 1
