from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message


from stw_gen_sample3_api.types.core.can.mode_type import Mode
from stw_gen_sample3_api.types.core.interfaces.stats_type import Stats


@dataclass
class Stats(_Message):

    # Режим работы CAN-драйвера
    mode: Mode
    # Статистика обмена по CAN-шине
    stat: Stats
    # Количество отправленных CAN-сообщений в очередь отправки
    sendToTxQueue: uint32_t


class StatsDict(TypedDict):

    # Режим работы CAN-драйвера
    mode: Union[Mode]

    # Статистика обмена по CAN-шине
    stat: Union[Stats]

    # Количество отправленных CAN-сообщений в очередь отправки
    sendToTxQueue: Union[uint32_t, int ]

