from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message


from stw_gen_sample3_api.types.core.debug.hard_fault_type import HardFault
from stw_gen_sample3_api.types.core.debug.stack_type import Stack


@dataclass
class HardFaultData(_Message):

    # Счётчик hardfault'ов
    count: uint32_t
    # Стек
    stack: Stack
    # Регистры с данными о hard fault
    hardFault: HardFault


class HardFaultDataDict(TypedDict):

    # Счётчик hardfault'ов
    count: Union[uint32_t, int ]

    # Стек
    stack: Union[Stack]

    # Регистры с данными о hard fault
    hardFault: Union[HardFault]

