from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message




@dataclass
class HardFault(_Message):

    # Configurable Fault Status Register, содержит MMSR (8 байт), BFSR (8 байт) and UFSR (16 байт)
    cfsr: uint32_t
    # Hard Fault Status Register
    hfsr: uint32_t
    # Debug Fault Status Register
    dfsr: uint32_t
    # Auxiliary Fault Status Register
    afsr: uint32_t
    # MemManage Fault Address Register
    mmar: uint32_t
    # Bus Fault Address Register
    bfar: uint32_t


class HardFaultDict(TypedDict):

    # Configurable Fault Status Register, содержит MMSR (8 байт), BFSR (8 байт) and UFSR (16 байт)
    cfsr: Union[uint32_t, int ]

    # Hard Fault Status Register
    hfsr: Union[uint32_t, int ]

    # Debug Fault Status Register
    dfsr: Union[uint32_t, int ]

    # Auxiliary Fault Status Register
    afsr: Union[uint32_t, int ]

    # MemManage Fault Address Register
    mmar: Union[uint32_t, int ]

    # Bus Fault Address Register
    bfar: Union[uint32_t, int ]

