from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message


from stw_gen_sample3_api.types.core.time.up_type import Up
from stw_gen_sample3_api.types.core.tm.msg_id_type import MsgId


@dataclass
class Item(_Message):

    # Идентификатор неисправности
    msgid: MsgId
    # Время первого возникновения
    ts: Up
    # Количество возникших неисправностей
    count: uint32_t


class ItemDict(TypedDict):

    # Идентификатор неисправности
    msgid: Union[MsgId]

    # Время первого возникновения
    ts: Union[Up]

    # Количество возникших неисправностей
    count: Union[uint32_t, int ]

