from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class BurnEvents(EnumType):
    """События прожига прошивки"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Неизвестная ошибка
    UnknownError = 0
    # Прошивание прошло успешно
    SuccessBurnFirmware = 1
    # Прошивка не была получена
    FirmwareNotReceived = 2
    # Неверная контрольная сумма
    WrongCheckSum = 3
    # Ошибка чтения или записи флеш-памяти
    FlashError = 4
    # Не удалось сохранить параметры загрузчика в MRAM
    ParamsSaveError = 5
