from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class Stage(EnumType):
    """Этапы загрузки файла прошивки"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Нет активного процесса
    Idle = 0
    # Идёт загрузка файла прошивки
    Receiving = 1
    # Прошивка загружена во временную область
    Received = 2
    # Загрузка файла прошивки была прервана
    Aborted = 3
