from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message




@dataclass
class Stats(_Message):

    # Количество принятых сообщений
    received: uint32_t
    # Количество отправленных сообщений
    transmitted: uint32_t
    # Количество ошибок приёма сообщения
    receivedErr: uint32_t
    # Количество ошибок отправки сообщений
    transmittedErr: uint32_t


class StatsDict(TypedDict):

    # Количество принятых сообщений
    received: Union[uint32_t, int ]

    # Количество отправленных сообщений
    transmitted: Union[uint32_t, int ]

    # Количество ошибок приёма сообщения
    receivedErr: Union[uint32_t, int ]

    # Количество ошибок отправки сообщений
    transmittedErr: Union[uint32_t, int ]

