from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class Corrupted(EnumType):
    """Сообщение о повреждении прошивки"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Прошивка загрузчика повреждена
    BootFwCorrupted = 0
    # Прошивка в основной области повреждена
    MainFwCorrupted = 1
    # Прошивка в области для загрузки повреждена
    UpdateFwCorrupted = 2
