from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message

from stw_gen_sample3_api.types.core.metainfo.bool__opt__type import bool_Opt_
from stw_gen_sample3_api.types.core.metainfo.uint_3_2_t__opt__type import uint32_t_Opt_
from stw_gen_sample3_api.types.core.sw.firmware_id__opt__type import FirmwareId_Opt_
from stw_gen_sample3_api.types.core.sw.version__opt__type import Version_Opt_


@dataclass
class Info(_Message):

    # Размер прошивки
    size: uint32_t_Opt_
    # CRC32 прошивки
    crc: uint32_t_Opt_
    # Версия прошивки
    version: Version_Opt_
    # Идентификатор прошивки
    id: FirmwareId_Opt_
    # Состояние целостности прошивки
    notCorrupted: bool_Opt_


class InfoDict(TypedDict):

    # Размер прошивки
    size: Union[uint32_t_Opt_]

    # CRC32 прошивки
    crc: Union[uint32_t_Opt_]

    # Версия прошивки
    version: Union[Version_Opt_]

    # Идентификатор прошивки
    id: Union[FirmwareId_Opt_]

    # Состояние целостности прошивки
    notCorrupted: Union[bool_Opt_]

