from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message

from stw_gen_sample3_api.types.core.periph3.state_type import State


@dataclass
class Additional(_Message):

    # Модуль АЦП
    adc: State
    # Таймер для АЦП
    adcTimer: State
    # Интерфейс взаимодействия с температурным датчиком
    tmp117Interface: State
    # Модуль температурного датчика
    tmp117: State
    # Таймер синхронизации времени в режиме master
    syncTimer: State


class AdditionalDict(TypedDict):

    # Модуль АЦП
    adc: Union[State]

    # Таймер для АЦП
    adcTimer: Union[State]

    # Интерфейс взаимодействия с температурным датчиком
    tmp117Interface: Union[State]

    # Модуль температурного датчика
    tmp117: Union[State]

    # Таймер синхронизации времени в режиме master
    syncTimer: Union[State]

