from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class Error(EnumType):
    """Типы ошибок инициализации периферии"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Ошибка инициализации критической периферии
    critical = 0
    # Ошибка инициализации важной периферии
    important = 1
    # Ошибка инициализации вспомогательной периферии
    additional = 2
