from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class State(EnumType):
    """Описание состояний периферии"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Периферия не инициализируется
    NotInitialized = 0
    # Провал при инициализации
    InitializedFailed = 1
    # Провал при автоматическом тесте по внутренней логике
    AutoSelfTestFailed = 2
    # Провал при тесте по команде
    CmdSelfTestFailed = 3
    # Предупреждение
    Warning = 4
    # Периферия работает штатно
    Normal = 5
