from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class WorkMode(EnumType):
    """Режим работы периферии"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Не инициализировать или игнорировать периферию
    NotUsing = 0
    # Использовать периферию по-умолчанию
    Normal = 1
    # Периферия принудительно используются
    Force = 2
