from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class Pattern(EnumType):
    """Паттерн для теста"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Паттерн 0xFF
    _FF = 0
    # Паттерн 0xAA
    _AA = 1
    # Паттерн 0x55
    _55 = 2
    # Паттерн 0x00
    _00 = 3
    # Счетчик с фиксированным инкрементом
    _Counter = 4
