from stw_py_api_runtime.gen_classes.types.enum_type import EnumType
from stw_gen_sample3_api.basic_types import *


class ResetReason(EnumType):
    """Причина сброса контроллера"""

    @classmethod
    def _base_type(cls):
        return uint8_t

    # Unknown
    unknown = 0
    # Low-Power
    lowPower = 1
    # Window watchdog
    wwdg = 2
    # Independent watchdog
    iwdg = 3
    # Software
    software = 4
    # Power-on-Reset / Power-down-Reset
    porPdr = 5
    # Pin
    pinReset = 6
    # Brown-out
    brownout = 7
