from dataclasses import dataclass
from typing import TypedDict, Union

from stw_gen_sample3_api.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message

from stw_gen_sample3_api.types.core.can.can_bus_type import CanBus
from stw_gen_sample3_api.types.core.sync.can2.mode_type import Mode
from stw_gen_sample3_api.types.core.time.gps_type import Gps
from stw_gen_sample3_api.types.core.time.up_type import Up


@dataclass
class Stats(_Message):

    # Активная CAN-шина для синхронизации времени
    bus: CanBus
    # Режим работы
    mode: Mode
    # Последнее сохраненное время
    savedSysTime: Gps
    # Последнее сохраненное время
    savedSteadyTime: Up


class StatsDict(TypedDict):

    # Активная CAN-шина для синхронизации времени
    bus: Union[CanBus]

    # Режим работы
    mode: Union[Mode]

    # Последнее сохраненное время
    savedSysTime: Union[Gps]

    # Последнее сохраненное время
    savedSteadyTime: Union[Up]

