"""Firmware tools hooks."""

import pytest


def pytest_addoption(parser: pytest.Parser):
    """Add custom cli arguments."""
    options_group: pytest.OptionGroup = parser.getgroup('stw-pro-test.frm options')
    options_group.addoption(
        '--frm_id_to_download',
        action='store',
        default=None,
        help='Firmware ID прошивки прибора. Будет загружена перед проведением проверок.'
        ' Должны быть указаны оба аргумента: frm_id_to_download и frm_version_to_download',
    )
    options_group.addoption(
        '--frm_version_to_download',
        action='store',
        default=None,
        help='Версия прошивки прибора. Будет загружена перед проведением проверок. '
        'Должны быть указаны оба аргумента: frm_version_to_download и frm_id_to_download',
    )
    options_group.addoption(
        '--flash_from_file',
        action='store',
        default=None,
        help='Путь до файла прошивки прибора. '
        'Если указан frm_id+frm_version прошивки, этот параметр будет проигнорирован.',
    )
