"""Bot messages for git."""
import pytest

from stw_pro_test.git.utils import get_git_branch, get_git_head, get_git_remote_origin


def get_git_branch_msg(config: pytest.Config) -> str:
    """Return git branch url."""
    git_origin = get_git_remote_origin(config.rootpath)
    git_branch = get_git_branch(config.rootpath)
    return f'- [git branch: {git_branch}]({git_origin}/-/blob/{git_branch})\n'


def get_git_head_msg(config: pytest.Config) -> str:
    """Return git head url."""
    git_origin = get_git_remote_origin(config.rootpath)
    short = get_git_head(config.rootpath)
    long = get_git_head(config.rootpath, short=False)
    return f'- [git head: {short}]({git_origin}/-/commit/{long})\n'
