"""Утилиты для работы с ботом."""
from collections.abc import Iterable


def put_escape_symbols(
        some_str: str,
        symbol_black_list: Iterable[str] = ('_', '[', ']', '*', '`', '~', '>'),
):
    """Экранируем неугодные символы из-за форматирования MarkdownV2."""
    for bad_symbol in symbol_black_list:  # Больное форматирование
        some_str = some_str.replace(bad_symbol, fr'\{bad_symbol}')
    return some_str
