"""Матчинг Цэ типов к питонячим."""
import itertools

int_like = [  # noqa: WPS317
    'uint8_t', 'uint16_t', 'uint32_t', 'uint64_t',
    'int8_t',  'int16_t',  'int32_t',  'int64_t',
    'ui8',     'ui16',     'ui32',     'ui64',
    'u8',      'u16',      'u32',      'u64',
    'i8',      'i16',      'i32',      'i64',
]

u_types = [  # noqa: WPS317
    '__u1',    '__u2',     '__u3',     '__u4',
    '__u5',    '__u6',     '__u7',     '__u8',
    '__u9',    '__u10',    '__u11',    '__u12',
    '__u13',   '__u14',    '__u15',    '__u16',
    '__u17',   '__u18',    '__u19',    '__u20',
    '__u21',   '__u22',    '__u23',    '__u24',
    '__u25',   '__u26',    '__u27',    '__u28',
    '__u29',   '__u30',    '__u31',    '__u32',
]

var_int_like = [  # noqa: WPS317
    'varint16_t', 'varint32_t', 'varint64_t',
    'varuint16_t', 'varuint32_t', 'varuint64_t',
]

single_float_like = ['float_type', 'f32']
double_float_like = ['double', 'f64']
other_like = [
    'bool', 'bytes',
]

c_like = ['bits', 'enum', 'struct', 'array', 'synonym']


# этот метод не должен использоваться, системные типы не используеются в сообщених
def matched_system_type(type_name) -> str | None:
    """Возвращает базовый python тип к C типу."""
    if type_name in int_like:
        return 'int'
    if type_name in itertools.chain(single_float_like, double_float_like):
        return 'float'
    if type_name == 'bool_type':
        return 'bool'
    return None
