"""Configuration file for stw-py-api-generator."""
import os

from environs import Env

path = os.path.join('', 'config', '.env')

env = Env()

if os.path.exists(path):
    env.read_env(path)

pipy_registry = env.str('PIPY_REGISTRY', 'https://nexus.1440.space/repository/pypiserver-proxy/')
versioning = env.bool('VERSIONING', False)  # noqa: WPS425
