"""Кастомные типы для генератора."""
import ast
from typing import Dict, List, TypedDict

from stw_py_api_generator.python_classes.generic import PythonModule


class CodeBlock(TypedDict):
    """Блок кода."""

    imports: List[ast.Import | ast.ImportFrom]
    content: List[str]  # noqa: WPS110


ModulePath = str
ModuleRegistry = Dict[ModulePath, PythonModule]

ModuleLevel = int
ModuleMap = Dict[ModuleLevel, List[PythonModule]]
