"""Описание типов."""
import dataclasses
from typing import Optional


@dataclasses.dataclass
class TypeDescription(object):
    """Описание типа в yaml файле."""

    info: str = ''
    full_path: str = ''


@dataclasses.dataclass
class SystemType(TypeDescription):
    """Типы, которые сводятся к системным/стандартным."""

    python_type: type = object
    alias: str = ''
    size: Optional[int] = None  # размер одного объекта в байтах
