"""Классы для описания устройства."""
from dataclasses import dataclass, field
from typing import List

from stw_py_api_generator.python_classes.generic import PythonClass


@dataclass
class DevicePythonClass(PythonClass):
    """Класс, экземпляр которого используется для управления устройством."""

    next_levels: List[str] = field(default_factory=list)
    device_name: str = 'unknown'


@dataclass
class SyncDevicePythonClass(DevicePythonClass):
    """Класс для описания прибора при генерации sync методов."""
