"""Заглушка для ещё не реализованных типов данных."""
from dataclasses import dataclass

from stw_py_api_generator import naming
from stw_py_api_generator.python_classes.generic import PythonClass


@dataclass
class NotImplementedPythonClass(PythonClass):
    """Класс-заглушка под ещё не реализованные типы."""

    type_kind: str = ''
    type_name: str = ''
    location: str = ''

    @classmethod
    def from_yaml_type(cls, yaml_desc):
        """Чтение из yml описания."""
        type_kind = yaml_desc.kind
        type_name = yaml_desc.name.name
        location = str(yaml_desc.name)
        file_name = naming.upper_to_snake(yaml_desc.name.name)

        return cls(
            type_kind=type_kind,
            type_name=type_name,
            location=location,
            custom_file_name=f'{file_name}_type.py',
        )
