"""Работа с перепрошивкой прибора."""
from dataclasses import dataclass

from stw_py_api_runtime.file_loading.loading import DataUploader, LoadMode
from stw_py_api_runtime.sdk.brokers.broker_api import BrokerAPI


@dataclass
class FirmwareUpdater:
    """Перепрошивка прибора."""

    broker: BrokerAPI
    device_id: int
    load_mode: LoadMode

    _chunk_size = 128
    _uploader: DataUploader


@dataclass
class FirmwareGetter:
    """Получение ZPAQ описания прошивки."""

    broker: BrokerAPI
    device_id: int
    load_mode: LoadMode

    _chunk_size = 100
    _uploader: DataUploader
