"""Десериализация команд и ответов на них."""
import base64

from stw_py_api_runtime.gen_classes.types.serializable_base import Serializable


class Empty(Serializable):
    """Представление объекта команды для десериализации.

    Заглушка для message_map у device.
    """

    @classmethod
    def from_bin(cls, bin_data: bytes) -> tuple:
        """Десериалиазция в объект из байтов."""
        base64_data = base64.b64encode(bin_data).decode('utf-8')
        return None, base64_data


Command = Empty
Response = Empty
TmReq = Empty
