from dataclasses import dataclass
from typing import TypedDict, Union

from stw_py_api_runtime.gen_shared_types.basic_types import *
from stw_py_api_runtime.gen_classes.telemetry.parse import Message as _Message




@dataclass
class FsInfo(_Message):

    # Состояние подключения
    mounted: bool_type
    # Емкость памяти
    capacity: uint32_t
    # Занято памяти
    usedSpace: uint32_t
    # Свободно памяти
    freeSpace: uint32_t


class FsInfoDict(TypedDict):

    # Состояние подключения
    mounted: Union[bool_type, bool ]

    # Емкость памяти
    capacity: Union[uint32_t, int ]

    # Занято памяти
    usedSpace: Union[uint32_t, int ]

    # Свободно памяти
    freeSpace: Union[uint32_t, int ]

