"""Ошибки внутри работы с сообщениями."""


class RpcError(Exception):
    """Ошибка взаимодействия с gRPC сервером."""


class RpcDeadlineError(RpcError):
    """SREDA не получила от прибора ответ вовремя."""


class UnsupportedSredaVersionError(Exception):
    """Несовместимая версия."""


class TmMsgError(Exception):
    """Ошибка при попытке получить TM сообщение."""


class EventMsgError(Exception):
    """Общий класс для описания любых ошибок при получении событий."""

    def __init__(self, event_name, error):
        self.event_name = event_name
        self.error = error


class DecodeError(Exception):
    """Ошибка при сериализации ТМ сообщения в объект."""


class InvalidBrokerError(Exception):
    """Используемый брокер не поддерживает этот функционал."""


class InvalidFirmwareError(Exception):
    """Ошибка в описании прошивки."""


class ResetDeviceError(Exception):
    """Ошибка при перезагрузке прибора."""


class DeserializeError(Exception):
    """Ошибка при десериализации сообщения в заданный тип."""


class SerializeError(Exception):
    """Ошибка при сериализации сообщения в заданный тип."""


class TmGettingError(Exception):
    """Ошибка при получении ТМ сообщения."""


class PygenNotFoundError(Exception):
    """Исключение, возникающее при невыполнении определенного условия."""


class BytesDecodeError(Exception):
    """Ошибка распаковки байт."""


class BytesEncodeError(Exception):
    """Ошибка упаковки в байты."""


class BitsDeserializeError(Exception):
    """Ошибка десериализации bits типа."""


class InitializationError(Exception):
    """Ошибка при инициализации конструктора класса."""


class CmdError(RpcError):
    """Ошибка отправки команды."""


class CancelledError(CmdError):
    """Отправка команды была отменена."""


class BadArgumentsError(CmdError):
    """Ошибка переданных аргументов."""


class SendCommandError(CmdError):
    """Ошибка ожидания ответа на команду."""


class ModuleNameError(CmdError):
    """CAN модуль не найден."""


class UnknownMessageError(CmdError):
    """Неизвестная тип сообщения."""


class ReceivedDataError(CmdError):
    """Некорректный ответ."""


class UnImplimentedError(CmdError):
    """Команда не реализована, либо не поддерживается."""


class ConnectError(RpcError):
    """Ошибка подключения к ресурсу."""


class TooManyCommandsError(Exception):
    """Слишком много команд."""


class NotSupportedError(NotImplementedError):
    """Неподдерживаемый кейс в текущей реализации."""


class VariantTypeError(Exception):
    """Ошибка при (де)сериализации variant типа."""


class OptionalTypeError(Exception):
    """Ошибка при (де)сериализации optional типа."""


class PredicateError(Exception):
    """Ошибки при работе с предикатами."""


class InvalidDeviceConfigError(Exception):
    """Ошибка при проверке текущего CAN с указанным по умолчанию."""


class VerifyModuleError(Exception):
    """Ошибка верификации модуля."""


class AmbiguityError(Exception):
    """Ошибка несоответствия."""


class DownloadFileError(Exception):
    """Ошибка при загрузке файле."""


class UnpackMessageError(Exception):
    """Ошибка при распаковке и обработке сообщения."""


class RepstubMessageError(Exception):
    """Сообщение является сообщением об ошибке."""


class MessageNotFoundError(Exception):
    """Сообщение не найдено в прошивке прибора."""


class ValidationError(Exception):
    """Ошибка валидации входных данных."""
