"""Средства для работы с сетью."""
from typing import Tuple


def get_ip_endpoint(host: str, port: int) -> str:
    """Слепить хост и порт в строку вида 127.0.0.1:2222 (IPv4) или [fec2::10]:33 (IPv6)."""
    # todo PLATSWRIG-2499 если host формата ipv6, то f'[{host}]:{port}'
    return f'{host}:{port}'


def get_host_port_from_ip(endpoint: str) -> Tuple[str, int]:
    """Вычленить хост и порт из строки вида 127.0.0.1:2222 (IPv4) или [fec2::10]:33 (IPv6)."""
    # todo PLATSWRIG-2499 если host формата ipv6
    host, port = endpoint.split(':')
    return host, int(port)
